mapper.select:
select count(id) from user_privilege_t where 1=1
and roleCode = #{roleCode}
and roleName = #{roleName}
and privilegeCode like CONCAT('%',#{privilegeCode},'%')

select 
	id,
	roleCode,
	roleName,
	privilegeCode
from 
	user_privilege_t
where 1=1

	and roleCode = #{param1.roleCode}


	and roleName = #{param1.roleName}


	and privilegeCode = #{param1.privilegeCode}

order by lastUpdatedDate desc
limit #{param2},#{param3}

update.foreach:
update user_privilege_t
roleCode=#{item.roleCode}, roleName=#{item.roleName}, privilegeCode=#{item.privilegeCode}, lastUpdatedBy=#{item.lastUpdatedBy}, lastUpdatedDate=now()
where id=#{item.id}
